function getQuote(){let t=randomIntFromInterval(0,quotes.length),e=quotes[t].text,o=quotes[t].author,n='"'+e+'" - '+o;document.querySelector("#text").textContent=e,document.querySelector("#author").textContent=o,document.querySelector("#author").setAttribute("title",o),document.querySelector("#tweet-quote").setAttribute("href","https://twitter.com/intent/tweet?text="+encodeURIComponent(n))}function randomIntFromInterval(t,e){return Math.floor(Math.random()*(e-t+1)+t)}const projectName="random-quote-machine";var quotes;localStorage.setItem("example_project","Random Quote Machine"),document.addEventListener("DOMContentLoaded",t=>{fetch("./assets/json/quotes.json",{method:"GET"}).then(t=>{if(t.ok)return t.json();throw"Error"}).then(t=>{quotes=t,getQuote()}).catch(t=>{console.log(t)}),document.querySelector("#new-quote").addEventListener("click",t=>{getQuote()})});